SELECT STANDARD_WT
FROM HWA_TBL
WHERE DIVISION_SEX = #{ls_sex}
AND STANDARD_FR_HIGH <= #{ll_fhit}
AND STANDARD_TO_HIGH >= #{ll_fhit}


SELECT OBE_SEQNO,OBE_DESC FROM OBE_M
